import React from 'react';
import {Component} from 'react';
import Header from './Header.jsx';
import './App.css';
import Homepage from './Homepage.jsx';
import Navi from './nav.jsx';
import Locations from './Locations.jsx';
import Locationdata from './Locationdata.jsx';
import './w3.css'




class App extends Component {
  constructor()
  {
    super();
    this.state=
    {
      id:'',
      converge:'',
      showHome:true,
      showLocation:false,
      showLoactiondata:false,
    }
  }

  Handle = (f) => 
  {
    this.setState({id:f});

  }

  homeid =(a) =>
  {
    this.setState({converge:a});
  }

  render()
{
  return (
    <div className="App">

     <Header heading="heading"/>
     <Navi id={this.state.id}/>

     <div id="main">
     {
       this.state.showHome?

     <div id="Home">
     <Homepage homeids={this.homeid}/>
     </div>
     :null
    }

     {
       this.state.showLocation?
     <div id="Location">
     <Locations ids={this.Handle}/>
      </div>
      :null
    }
      {
       this.state.showLocationdata?
       <div id="Locationdata">
     <Locationdata/>
     </div>
            :null
            }
     </div>






    </div>
  );
}
}

export default App;
