import React from 'react';
import {Component} from 'react';
import Header from './Header.jsx';
import './App.css';
import Homepage from './Homepage.jsx';
import Navi from './nav.jsx';
import Locations from './Locations.jsx';
import Locationdata from './Locationdata.jsx';
import './w3.css'




class App extends Component {
  constructor()
  {
    super();
    this.state=
    {
      id:'',
      homeid:''
    }
  }


  Handle = (f) => 
  { 
    this.setState({id:f});
  }

  Homehandler=(a) =>
  {
    this.setState({homeid:a});
  }

  maincontainhandler =() =>
  {
    if(this.state.homeid===1)
    {
      document.getElementById("Location").display="none";
      document.getElementById("Locationdata").display="none";
    }
  }


  render()
{
  return (
    <div className="App">

     <Header heading="heading"/>
     <Navi id={this.state.id}/>

     <div id="main" onLoad={this.maincontainhandler()}>


     <div id="Home">
     <Homepage homeids={this.homeid}/>
     </div>
     <div id="Location">
     <Locations ids={this.Handle}/>
      </div>
       <div id="Locationdata">
     <Locationdata/>
     </div>

     </div>






    </div>
  );
}
}

export default App;
